package Users;

public class Address {
	
	private double x;
	private double y;

	public Address() {
		// TODO Auto-generated constructor stub
	}

	/**
	 * address class
	 * @param x : x coordinate
	 * @param y : y coordinate
	 */
	public Address(double x, double y) {
		super();
		this.x = x;
		this.y = y;
	}

	public double getX() {
		return x;
	}

	public void setX(double x) {
		this.x = x;
	}

	public double getY() {
		return y;
	}

	public void setY(double y) {
		this.y = y;
	}
	
	
	
	

}
